<script>
import axios from "axios";
export default{
    name:'adminHome',
    components:{

    },
    data(){
        
        return {
           department:[],
           numberOfDepartment:'',
           chairs:[],
           faculty:[],
           year:'1',
           numberOfFaculty:'',
           numberOfChairs:'',
           numberOfTotalProject:'',
           numberOfTotalStudent:'',
           numberOfCurrentProject:'',
           numberOfCurrentStudent:'',
           numberOfStaffMember:'',
           divStyle:'m-8  border-solid border-2 border-blue-700 rounded-md bg-gray-200 ',
               }
          
    },
   async mounted(){
    let token=localStorage.getItem('token')    
      if(!token){
       this.$router.push({name:'Login'})
    }
    let dat = new Date();
        this.year = dat.getFullYear();
        console.log(this.year)
        console.log(dat)
        await axios.get(`http://localhost:5000/adminDepartmentList`)
          .then(response => {
            this.department = response.data;
             this.numberOfDepartment = response.data.length;
         
         console.log(this.stream)
       })
       await axios.get(`http://localhost:5000/adminChairList`)
          .then(response => {
            this.chairs = response.data;
             this.numberOfChairs = response.data.length;
         
         console.log(this.stream)
       })
       await axios.get(`http://localhost:5000/adminListLength/students/date/${this.year}`)
          .then(response => {
            this.numberOfTotalStudent = response.data[0].length;
             this.numberOfCurrentStudent = response.data[1].length;
         
         console.log(this.stream)
       })
       await axios.get(`http://localhost:5000/adminListLength/staff_member/department_id/1`)
          .then(response => {
            this.numberOfStaffMember = response.data[0].length;
            
         
         console.log(this.stream)
       })
       await axios.get(`http://localhost:5000/adminListLength/allProject/date/${this.year}`)
          .then(response => {
            this.numberOfTotalProject = response.data[0].length;
             this.numberOfCurrentProject = response.data[1].length;
         
         console.log(this.stream)
       })
       await axios.get(`http://localhost:5000/adminFacultyList`)
          .then(response => {
            this.faculty = response.data;
             this.numberOfFaculty = response.data.length;
         
         console.log(this.stream)
       })
       
      
    }

}
</script>